import React from "react";
import Root from './Pages/Root';
import About from './Pages/About';
import Contact from './Pages/Contact';
import Post from './Pages/Post';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";
import { slide as Menu } from 'react-burger-menu'
import './style.css';
import './menu-style.css';
import postData from "./postData.json";
import { json } from "react-router-dom";

function App() {

  const router = createBrowserRouter([
    {
      path: "/",
      element: <Root />,
    },
    {
    path: "about",
    element: <About />,
    },
    {
    path: "contact",
    element: <Contact />,
    },
    {
    path: "post",
    element: <Post />,
    }
  ]);

  const loader = async () => {
  const data = getSomeData();
  return json(data);
  };

  const idRouter = postData.map(post =>
      {
        path: `posts/${postData.id}`
      }
    )

  return (
    <div className="App">
      <header>
        <Menu>
          <Link id="home" className="menu-item" to={"/"}>Home</Link>
          <Link id="about" className="menu-item" to={"/about"}>Sobre</Link>
          <Link id="contact" className="menu-item" to={"/contact"}>Contato</Link>
        </Menu>
      </header>
      <RouterProvider className="container" router={router} />
    </div>
  );
}

export default App;
